sp_widget.client_script:
function($scope, $rootScope, $http) {
  /* widget controller */
  var c = this;

	$scope.updatePrefDept = function() {
		$http.get('/api/x_saico_eppersona/client_utils/setUserPrefDept?prefDept='+c.data.prefDept)
			.then(function(res) {
				location.href = "/oc";
		});	
	};


	$scope.toggleSummaryView = function() {
		var showVal;
		if (c.data.prefShowSummary) {
			showVal = "no";
		} else {
			showVal = "yes";
		}
		$http.get('/api/x_saico_eppersona/client_utils/setUserPrefShowSummary?showSummary='+showVal)
			.then(function(res) {
				location.href = "/oc";
		});	
	};
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	// data.prefDept = "OCPW";

	var smu = new SSPMegaUtil();

	data.prefDept = smu.getUserDepartment();
	data.prefShowSummary = smu.getUserShowSummary();
	gs.info("prefDept: " + data.prefDept + "\nprefShowSummary: " + data.prefShowSummary);
	// Get list of Departments
	data.departments = ['ALL'];
	var gr = new GlideRecord('cmn_department');
	gr.addEncodedQuery('u_active=true');
	gr.orderBy('name');
	gr.query();

	while (gr.next()) {
		data.departments.push(gr.getDisplayValue('name'));
	}


})();
