sp_widget.client_script:
function($scope, $rootScope, $window) {
  /* widget controller */
  var c = this;


	// Functions that emit events
	c.onHelpClick = function() {
		// $rootScope.$broadcast('sspHelpClick', "CustomSearch");
		window.location = '/oc?id=sc_category&catalog_id=866b692f4fc01340a6bd5f211310c703&sys_id=0ec4cf6d6f0a15007d1f35af1e3ee428';
	};

	c.onServicesClick = function() {
		// $rootScope.$broadcast('sspServicesClick', "CustomSearch");
		window.location = '/oc?id=sc_category&sys_id=1f14c3ad6f0a15007d1f35af1e3ee4da';
	};

	c.onAccessClick = function() {
		// $rootScope.$broadcast('sspAccessClick', "CustomSearch");
		window.location = '/oc?id=sc_category&sys_id=4d948f6d6f0a15007d1f35af1e3ee4ff';
	};

	c.onAnswersClick = function() {
		// $rootScope.$broadcast('sspAnswersClick', "CustomSearch");
		window.location = '/oc?id=kb_view2&kb_category=&kb_id=25c2ccaedb4cd700b83e790aaf9619d6';
	};



}

