sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	data.userFirst = gs.getUser().getFirstName();
	data.userTimeZone = gs.getSession().getTimeZoneName();
	data.bgImage = options.image ||  'oc-background.png';
	data.backgroundImage = {
		"background-image": "url(" + data.bgImage + ")",
		"background-position": options.bgImagePosition || 'center', 
		"background-attchment" : options.bgImageAttachment || 'local',
		"background-repeat": options.bgImageRepeat || 'no-repeat', 
		"background-size" : options.bgImageSize || 'cover',
		"margin-top": "0px",
		"margin-bottom" : "5px"

	};

	//if true shows font awesome icons on the buttons
	//else we show the images
	data.fAwesome = false;

})();
