sp_widget.client_script:
function($scope, $http) {

	var c = this;

	// Replace hard coded DIV id with dynamic one
	c.data.mcdid = "CD"+Math.random()*1000000;
	document.getElementById('replaceMeInClientScript')
		.setAttribute("id", c.data.mcdid);	

	// Create the chart object without
	// data, enable the "Loading" indicator.
	c.myChart = {};
	_createChart();
	c.myChart.showLoading();

	// Make call to appropriate API to get
	// data. Update chart and disable 
	// "Loading" indicator when data is returned
	$http.get('/api/x_saico_eppersona/client_utils/incidentPieData?aggField='+encodeURIComponent(c.options.aggregate_field)+'&aggFilter='+encodeURIComponent(c.options.aggregate_filter)+"&cache_name="+c.options.cache_name)
	// $http.get('/api/x_saic4_persona_ss/client_utils/incidentPieData?aggField='+c.options.aggregate_field+'&aggFilter='+c.options.aggregate_filter)
			.then(function(res) {
				c.data.chartData = res.data.result;
				c.myChart.series[0].setData(c.data.chartData);
				c.myChart.hideLoading();
			}
	);	


	// Function to create and render the
	// chart object.
	function _createChart() {

		c.myChart = new Highcharts.Chart({
			chart: {
				type: 'pie',
				renderTo: c.data.mcdid
			},
			title: {
				text: c.options.title
			},
			exporting: {
				enabled: false
			},
			credits: {
				enabled: false
			},
			loading: {
				hideDuration: 1000,
				showDuration: 1000
			},
			tooltip: {
				pointFormat: 'Count: <b>{point.y}</b><br/>Percent: <b>{point.percentage:.1f}%</b>'
			},
			legend: {
				enabled: c.options.enable_legend,
				align: 'left',
				verticalAlign : 'middle',
				labelFormatter: function () {
						if (this.name.length > 10) {
							return this.name.substring(0, 10) + '...';
						} else {
							return this.name;
						}
        }
			},
			plotOptions: {
				pie: {
					allowPointSelect: true,
					cursor: 'pointer',
					depth: 15,
					dataLabels: {
						enabled: true,
						format: '<b>{point.y}</b>',
						distance: 5,
						style: {
							color: 'black'
						}
					},
					showInLegend: true
				},
				series: {
					point: {
						events: {
							click: function() {
								if (c.data.hasITIL) {
									var aggVal = this.options.name;
									if (c.options.aggregate_field == "priority") {
										aggVal = this.options.name.substring(9,10);
									}
									location.href = "/nav_to.do?uri=%2F"+c.options.table_name+"_list.do%3Fsysparm_query="+c.options.aggregate_filter+"%5E"+c.options.aggregate_field+"%3D"+encodeURIComponent(aggVal);
									// location.href = "/oc?id=list&&table="+c.options.table_name+"&sysparm_query="+c.options.aggregate_filter+"%5E"+c.options.aggregate_field+"%3D"+this.options.name;
								}
							}
						}
					}					
				}
			},
			series: [{
				name: c.options.aggregate_name,
				colors: c.data.chartColors,
				data: c.data.chartData
			}]
		});	
	}



}

