sp_widget.client_script:
function($scope, $http) {

	var c = this;

	// Replace hard coded DIV id with dynamic one
	c.data.mcdid = "CD"+Math.random()*1000000;
	document.getElementById('replaceMeInClientScript')
		.setAttribute("id", c.data.mcdid);	

	// Create the chart object without
	// data, enable the "Loading" indicator.
	c.myChart = {};
	_createChart();
	c.myChart.showLoading();

	var sbEnabled = false;
	// Make call to appropriate API to get
	// data. Update chart and disable 
	// "Loading" indicator when data is returned
	$http.get('/api/x_saico_eppersona/client_utils/getBarScrollData?aggField='+encodeURIComponent(c.options.aggregate_field)+'&aggFilter='+encodeURIComponent(c.options.aggregate_filter)+'&table='+encodeURIComponent(c.options.table_name)+'&sortbycount='+encodeURIComponent(c.options.sortbycount)+"&cache_name="+c.options.cache_name)
	// $http.get('/api/x_saic4_persona_ss/client_utils/incidentPieData?aggField='+c.options.aggregate_field+'&aggFilter='+c.options.aggregate_filter)
			.then(function(res) {
				c.data.chartData = res.data.result;
		/**
				var cdl = c.data.chartData.length;
				if (cdl < 3) {
					c.myChart.xAxis.scrollbar.enabled = false;
				} else {
					c.myChart.xAxis.scrollbar.enabled = true;
				}
			**/
				c.myChart.series[0].setData(c.data.chartData);
				c.myChart.hideLoading();
			}
	);	


	// Function to create and render the
	// chart object.
	function _createChart() {

		c.myChart = new Highcharts.Chart({
			chart: {
				type: 'bar',
				renderTo: c.data.mcdid
			},
			title: {
				text: c.options.title
			},
			exporting: {
				enabled: false
			},
			credits: {
				enabled: false
			},
			loading: {
				hideDuration: 1000,
				showDuration: 1000
			},
			xAxis: {
					type: 'category',
					title: {
							text: null
					},
					min: 0,
					max: 2,
					scrollbar: {
							enabled: true
					},
					tickLength: 0
		},
			yAxis: {
					min: 0,
					// max: 1200,
					title: {
							enabled: false,
							text: 'Votes',
							align: 'high'
					}
			},
			plotOptions: {
					series: {
						color: '#3B6376',
						// color: c.data.chartColors,
						point: {
							events: {
								click: function() {
									if (c.data.hasITIL) {
										if (this.options.name == "Inquiry / Help") {this.options.name = "inquiry";}
										location.href = "/nav_to.do?uri=%2F"+c.options.table_name+"_list.do%3Fsysparm_query="+c.options.aggregate_filter+"%5E"+c.options.aggregate_field+"%3D"+this.options.name;
										// location.href = "/oc?id=list&&table="+c.options.table_name+"&sysparm_query="+c.options.aggregate_filter+"%5E"+c.options.aggregate_field+"%3D"+this.options.name;
									}
								}
							}
						}
					},
					bar: {
							dataLabels: {
									enabled: true
							}
					}
			},
			legend: {
					enabled: false
			},
			series: [{
					// name: 'Votes',
					data: [
							["Loading", 50]
					]
			}]
		});	
	}



}

