(function() {

	// Get a unique id for the chart's
	// containing DIV.
	// data.mcdid = gs.generateGUID();
	// data.mcdid = "CD"+Math.random()*100000;

	var spc = new SSPChartUtils();
	data.chartColors = spc.getPaletteByName(options.palette);

	data.chartData = [];

	data.hasITIL = gs.getUser().hasRole('itil');

})();
