	getUserDepartment: function() {
		// var uDept = gs.getUser().getRecord().getDisplayValue('department');
		var userPrefDept = gs.getUser().getPreference('epsp_preferred_department');
		if (!userPrefDept) {
			var gr= new GlideRecord('sys_user');
			gr.get(gs.getUserID());
			return gr.getDisplayValue('department');
		}
		return userPrefDept;
	},

	getUserShowSummary: function() {
		var userPrefShowSummary = gs.getUser().getPreference('epsp_show_summary');
		if (!userPrefShowSummary || userPrefShowSummary == "yes") {
			return true;
		} else {
			return false;
		}	
	},


