	///////////////////////////////////////////
	// Given a template's title, returns the
	// index of that template's object within
	// the provided list of templates
	getTemplateIndexFromTitle: function(title, templates) {
		for (var i = 0; i < templates.length; i++) {
			if (templates[i]["title"] === title) {
				return i;
			}
		}
		return 0;
	},


